'''
Minimum Window Substring

Given a string S and a string T, find the minimum window in S which will contain all the characters in T in complexity O(n).

Example:

Input: S = "ADOBECODEBANC", T = "ABC"
Output: "BANC"
Note:

If there is no such window in S that covers all characters in T, return the empty string "".
If there is such window, you are guaranteed that there will always be only one unique minimum window in S.
'''


class Solution:
    def minWindow(self, s, t):
        """
        :type s: str
        :type t: str
        :rtype: str
        """

        if not s or not t:
            return ''

        wordDic = {}
        for word in t:
            if word not in wordDic:
                wordDic[word] = 0
            wordDic[word] += 1
        tempDic = {} #保存所有被遍历的情况
        count = 0
        minL = len(s) + 1
        minKey = -1
        print(wordDic)
        for i in range(len(s)):
            if s[i] in wordDic:

                for key in tempDic:
                    if tempDic[key][2] == 0:
                        continue
                    if i - tempDic[key][1] + 1 >= minL:
                        continue
                    if tempDic[key][0][s[i]] != 0:
                        tempDic[key][0][s[i]] -= 1
                        tempDic[key][2] -= 1
                        if tempDic[key][2] == 0:
                            if i - tempDic[key][1] + 1 < minL:
                                minL = i - tempDic[key][1] + 1
                                minKey = key
                tempDic[count] = [wordDic.copy(),i,len(t) - 1]
                tempDic[count][0][s[i]] -= 1
                if tempDic[count][2] == 0:
                    if i - tempDic[count][1] + 1 < minL:
                        minKey = count
                        minL = i - tempDic[count][1] + 1
                count += 1

        if minKey == -1:
            return ''
        return s[tempDic[minKey][1]:tempDic[minKey][1] + minL]


import collections
class Solution1:
    def minWindow(self, s, t):
        print(t,s)
        need, missing = collections.Counter(t), len(t)
        i = I = J = 0
        for j, c in enumerate(s, 1):
            print(need)
            missing -= need[c] > 0
            print(missing)
            need[c] -= 1
            if not missing:
                while i < j and need[s[i]] < 0:
                    print('cur,',need)
                    need[s[i]] += 1
                    i += 1
                if not J or j - i <= J - I:
                    I, J = i, j
        return s[I:J]

